<script>
import wepy from "wepy";
import dayjs from "dayjs";
import { shttp } from "../utils/http";
import { SHOPID } from "../constant/configConstant";
import { showSuccessToast, showFailToast, exploitToast } from "../utils/tools";
export default class Home extends wepy.page {
  config = {
    enablePullDownRefresh: true,
    navigationBarTitleText: "首页"
  };
  data = {
    typeList: [],
    showShop: false,
    bannerList: [],
    indicatorDots: true,
    autoplay: true,
    interval: 3000,
    duration: 500,
    typeList: [],
    storeData: null,
    imgNum: 3,
    imgInterval: 210,
    goodsList: [],
    customNav: [],
    shopId: null,
    discountList: []
  };

  components = {};
  onLoad(options) {
    let shopId = wx.getStorageSync("shopId");
    this.shopId = shopId;
    if (SHOPID) {
      this.shopId = SHOPID
      this.showShop = true;
      wx.setStorageSync("shopId", SHOPID);
    }
  }
  onShow() {
    console.log(this.shopId);
    this.shopId = wx.getStorageSync("shopId");
    if (SHOPID) {
      this.showShop = true;
      wx.setStorageSync("shopId", SHOPID);
      this.getbannerList(SHOPID);
      this.getnavList(SHOPID);
      this.getstoreData(SHOPID);
      this.getgoodsList(SHOPID);
      this.getDiscountList(SHOPID);
    } else {
      if (this.shopId) {
        this.showShop = true;
        this.getbannerList(this.shopId);
        this.getnavList(this.shopId);
        this.getstoreData(this.shopId);
        this.getgoodsList(this.shopId);
        this.getDiscountList(this.shopId);
      } else {
        this.geTypeList();
        this.showShop = false;
      }
      this.$apply();
    }
  }
  methods = {};
  goShopList(e) {
    let type = e.currentTarget.dataset.id;
    wx.reLaunch({
      url: `shopList?shopType=${type}`
    });
  }

  //获取店铺分类
  async geTypeList() {
    const res = await shttp
      .get("/api/v1/member/storeclass")
      .query({})
      .end();
    if (res.status === 0) {
      this.typeList = res.data;
      wx.hideLoading();
      wx.stopPullDownRefresh();
    } else {
      wx.hideLoading();
      wx.stopPullDownRefresh();
    }
    this.$apply();
  }

  //获取轮播图
  async getbannerList(shopId) {
    const res = await shttp
      .get(`/api/v1/member/banner?store_id=${shopId}`)
      .end();
    this.bannerList = res.data;
    wx.stopPullDownRefresh();
    this.$apply();
  }
  //获取自定义导航
  async getnavList(shopId) {
    const res = await shttp
      .get(`/api/v1/member/storegc?store_id=${shopId}`)
      .end();
    this.customNav = res.data;
    wx.stopPullDownRefresh();
    this.$apply();
  }
  //获取商品列表
  async getgoodsList(shopId) {
    const res = await shttp
      .get(`/api/v1/member/goodscommon?store_id=${shopId}`)
      .query({
        page: 1,
        goods_commend: 1
      })
      .end();
    if (res.status === 0) {
      this.goodsList = res.data;
      wx.hideLoading();
      wx.stopPullDownRefresh();
    } else {
      wx.hideLoading();
      wx.stopPullDownRefresh();
    }
    this.$apply();
  }
  //获取店铺详情
  async getstoreData(shopId) {
    const res = await shttp
      .get(`/api/v1/member/storeinfo?store_id=${shopId}`)
      .end();
    this.storeData = res.data;
    if (this.storeData.store_images.length < 3) {
      this.imgNum = this.storeData.store_images.length;
      this.imgInterval = 300;
    } else {
      this.imgNum = 3;
      this.imgInterval = 210;
    }
    wx.stopPullDownRefresh();
    this.$apply();
  }
  //跳转商品详情
  goGoodsDetails(e) {
    let id = e.currentTarget.dataset.id;
    wx.navigateTo({
      url: `store/goodsDetails?goods_commonid=${id}&type=sale`
    });
  }
  //轮播图跳转
  swipclick(e) {
    let imgurl = e.currentTarget.dataset.imgurl;
    if (imgurl) {
      wx.navigateTo({
        url: `advertisingPage?imgurl=${imgurl}`
      });
    }
  }
  //自定义页
  goCustomPage(e) {
    let storegc_id = e.currentTarget.dataset.id;
    wx.navigateTo({
      url: `/pages/store/goodsList?type=custom&id=${storegc_id}`
    });
  }

  //领取优惠券
  async getDiscounts(e) {
    let id = e.currentTarget.dataset.id;
    let index = e.currentTarget.dataset.index;
    const res = await shttp
      .get(`/api/v1/member/acquireCoupon?vouchertemplate_id=${id}`)
      .end();
    if (res.status === 0) {
      this.discountList[index].vouchertemplate_giveout++;
      showSuccessToast("领取成功");
    } else {
      // showFailToast(res.error);
      return wx.showToast({
        title: res.error,
        icon: "none",
        duration: 1000
      });
    }
    this.$apply();
  }
  //获取店铺优惠券
  async getDiscountList(shopId) {
    const res = await shttp
      .get(`/api/v1/member/isgetCoupon?store_id=${shopId}`)
      .query({
        page: 1
      })
      .end();
    this.discountList = [];
    this.discountList = this.discountList.concat(res.data[0]);
    this.$apply();
  }

  //打电话
  makePhoneCall() {
    wx.makePhoneCall({
      phoneNumber: this.storeData.store_phone
    });
  }
  //查看店铺大图
  bigImg(e) {
    const index = e.currentTarget.dataset.index;
    const images = e.currentTarget.dataset.images;
    wx.previewImage({
      current: images[index],
      urls: images
    });
  }
  //下拉刷新
  onPullDownRefresh() {
    if (SHOPID) {
      this.showShop = true;
      wx.setStorageSync("shopId", SHOPID);
      this.getbannerList(SHOPID);
      this.getnavList(SHOPID);
      this.getstoreData(SHOPID);
      this.getgoodsList(SHOPID);
      this.getDiscountList(SHOPID);
    } else {
      if (this.shopId) {
        this.showShop = true;
        this.getbannerList(this.shopId);
        this.getnavList(this.shopId);
        this.getstoreData(this.shopId);
        this.getgoodsList(this.shopId);
        this.getDiscountList(this.shopId);
      } else {
        this.geTypeList();
        this.showShop = false;
      }
    }
    this.$apply();
  }
  noOpened() {
    exploitToast("升级更新中");
  }
  //singnin
  async shareSignIn() {
    const userInfo = await signIn(false, this.shareId);
  }
  onShareAppMessage() {
    let path = "/pages/home";
    let shopId = wx.getStorageSync("shopId");
    return {
      title: "",
      path: `/pages/authorization?path=${path}&shopId=${shopId}`,
